// File: Views/Components/SliderItemView.swiftimport SwiftUIstruct SliderItemView: View {    let item: SliderItem    let isCenter: Bool        var body: some View {        VStack(spacing: 12) {            // Circle with Icon - Properly Centered            ZStack {                // Outer White Circle                Circle()                    .fill(Color.white)                    .frame(width: isCenter ? 120 : 80, height: isCenter ? 120 : 80)                    .shadow(color: Color.black.opacity(0.15), radius: isCenter ? 12 : 8, x: 0, y: isCenter ? 6 : 4)                                // Inner Colored Circle                Circle()                    .fill(item.backgroundColor)                    .frame(width: isCenter ? 100 : 60, height: isCenter ? 100 : 60)                                // Icon - Centered in the circles                Image(systemName: item.iconName)                    .font(.system(size: isCenter ? 40 : 24, weight: .medium))                    .foregroundColor(item.iconColor)            }                        // Title            Text(item.title)                .font(isCenter ? .headline : .caption)                .fontWeight(isCenter ? .semibold : .medium)                .foregroundColor(item.iconColor)                .multilineTextAlignment(.center)                .frame(maxWidth: 100)                .opacity(isCenter ? 1.0 : 0.7)        }        .animation(.spring(response: 0.4, dampingFraction: 0.7), value: isCenter)    }}